import React, {useContext} from "react";
import { FormContext } from './FormContext';

const Input = ({ field_id, field_label, field_placeholder, field_value }) => {
    return (
        <div className="mb-3">
          <label htmlFor="exampleFormControlInput1" className="form- label">{field_label}</label>
          <input type="text" className="form-control" id="exampleFormControlInput1" placeholder={field_placeholder ? field_placeholder : ''} value={field_value} />
        </div>
    );
}

export default Input;
